@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'currency-converter';
  currjson:any=[];
  base='USD';
  cont2='USD';
  result:string='1';



  changebase(a:string){
    this.base=a;
    console.log();

  }
}
